(function() {
    
    ip.game.AudioAutoplayer = {
        init: init,
        launch: launch,
        run: run
    };

    var ButtonHandler = ip.game.ButtonHandler;

    var index = -1;
    var buttons;

    function init() {

        buttons = ButtonHandler.getAudioButtons();
        try {
            initPlaya();
        } catch (err) {
        	console.log("Error initPlaya: " + err.message);
        }
    }

    function launch() {
        // Playa needs some time before the first play. Otherwise the first audio isn't played.
        setTimeout('ip.game.AudioAutoplayer.run();', 250);
    }

    function initPlaya() {
        // Without start() Playa doesn't notify about its states
        Playa.start();
        Playa.onPlayStop = onPlayStop;
    }

    function run() {
        if (next()) {
            play();
        } else {
            index = -1;
        }
    }

    function next() {

        index++;

        return index < buttons.length;
    }

    function play() {
        if (canPlay()) {
            playButton();
        } else {
            run();
        }
    }

    function playButton() {

        var button = getAudioButton();

        button.trigger('click');
        button.addClass('btn-warning');
    }

    function onPlayStop() {
        if (isPlayingState()) {
            stopButton();
            run();
        }
    }

    function stopButton() {
        getAudioButton().removeClass('btn-warning');
    }

    function canPlay() {

        var toggleButton = $(buttons[index].toggleButton);

        var visible = toggleButton.is(":visible");
        var checked = toggleButton.hasClass('btn-success');

        return visible && checked;
    }

    function getAudioButton() {
        return $(buttons[index].playButton);
    }

    function isPlayingState() {
        return index > -1;
    }

})();


