(function() {

    ip.game.matching.Draggable = {

        orderedElementIds: ['draggable0', 'draggable1', 'draggable2', 'draggable3'],

        init: init,
        reset: reset,
        disable: disable
    };

    var Draggable = ip.game.matching.Draggable;

    var offsetStep = 49; // i.e. button height
    var offsets = [0, 49, 2*49, 3*49, 4*49, 5*49];

    function init() {
        makeAllDraggable();
    }

    function reset() {
        for (var i = 0; i < 6; i++) {
            putElementAtIndex('draggable'+i, i);
            $('#draggable'+i).draggable('enable');
        }
    }

    function disable() {
        for (var i = 0; i < 6; i++) {
            $('#draggable'+i).draggable('disable');
        }
    }

    function makeAllDraggable() {
        for (var i = 0; i < 6; i++) {
            makeDraggable('#draggable'+i);
        }
    }

    function makeDraggable(id) {
        $(id).draggable({
            containment: "#drag-container",
            stack: '#drag-container a',
            axis: "y",
            stop: function(event, ui) {
                swapElements(this.id, ui.offset.top);
            }
        });
    }

    function swapElements(id, elementOffsetTop) {
        putAnotherAtOldIndex(id, elementOffsetTop);
        putDraggedAtNewIndex(id, elementOffsetTop);
    }

    function putAnotherAtOldIndex(id, elementOffsetTop) {

        var anotherId = getAnotherElementId(elementOffsetTop);
        var oldIndex = $.inArray(id, Draggable.orderedElementIds);

        putElementAtIndex(anotherId, oldIndex);
    }

    function getAnotherElementId(elementOffsetTop) {

        var newIndex = getOffsetIndex(elementOffsetTop);

        return Draggable.orderedElementIds[newIndex];
    }

    function putDraggedAtNewIndex(id, elementOffsetTop) {

        var newIndex = getOffsetIndex(elementOffsetTop);

        putElementAtIndex(id, newIndex);
    }

    function putElementAtIndex(id, index) {

        $('#'+id).offset({
            top: getOffsetTop(index)
        });

        Draggable.orderedElementIds[index] = id;
    }

    function getOffsetTop(index) {
        return getContainerOffsetTop() + offsets[index];
    }

    function getContainerOffsetTop() {
        return $('#drag-container').offset().top;
    }

    function getOffsetIndex(elementOffsetTop) {

        var diff = elementOffsetTop - getContainerOffsetTop();

        return Math.round(diff / offsetStep);
    }

})();
