(function() {

    ip.game.picture.Shuffler = {
        reset: reset,
        next: next,
        getWordTranslation: getWordTranslation,
        getWordTranslations: getWordTranslations,
        isCorrect: isCorrect
    };

    var SHUFFLE = [
        [2, 0, 1],
        [1, 2, 0],
        [0, 1, 2],
        [2, 1, 0],
        [1, 0, 2],
        [0, 2, 1]
    ];

    var ANSWERS = [1, 0, 2, 2, 0, 1];

    var index = -1;
    var wordTranslations;

    function reset(_wordTranslations) {
        wordTranslations = _wordTranslations;
        next();
    }

    function next() {

        index++;

        if (index == ANSWERS.length) {
            index = 0;
        }
    }

    function getWordTranslation() {

        if (wordTranslations == null) {
            return null;
        }

        var i = SHUFFLE[index][ ANSWERS[index] ];

        return wordTranslations[i];
    }

    function getWordTranslations() {
        return [
            wordTranslations[ SHUFFLE[index][0] ],
            wordTranslations[ SHUFFLE[index][1] ],
            wordTranslations[ SHUFFLE[index][2] ]
        ];
    }

    function isCorrect(i) {
        return i == ANSWERS[index];
    }

})();
(function() {

    ip.game.picture.Model = {
        init: init,
        next: next,
        getWordTranslation: getWordTranslation,
        getWordTranslations: getWordTranslations,
        isCorrect: isCorrect,
        getPrevWordTranslation: getPrevWordTranslation
    };

    var Storage = ip.game.WordTranslationStorage;
    var SubArray = ip.game.picture.SubArray;
    var Shuffler = ip.game.picture.Shuffler;
    var ImagePreloader = ip.game.picture.ImagePreloader;

    var index = 2;
    var prevWordTranslation;

    function init() {
        Storage.setSortable(false);
        SubArray.init(Storage.size());
        ImagePreloader.init();
    }

    function next() {

        prevWordTranslation = Shuffler.getWordTranslation();

        if (hasNext()) {
            doNext();
        } else {
            reset();
        }
    }

    function hasNext() {
        return index < 2;
    }

    function reset() {

        if (SubArray.isCompleted()) {
            // All words and images have been viewed. Now the words and images can be shown in any order.
            Storage.setSortable(true);
        }

        index = 0;
        setNextWordTranslations();
        ImagePreloader.loadNext();
    }

    function setNextWordTranslations() {

        var indexArray = SubArray.isCompleted() ? [0, 1, 2] : SubArray.next();
        var wts = Storage.subArray(indexArray);

        Shuffler.reset(wts);
    }

    function doNext() {
        index++;
        Shuffler.next();
    }

    function getWordTranslation() {
        return Shuffler.getWordTranslation();
    }

    function getWordTranslations() {
        return Shuffler.getWordTranslations();
    }

    function isCorrect(i) {
        return Shuffler.isCorrect(i);
    }

    function getPrevWordTranslation() {
        return prevWordTranslation;
    }

})();
