(function() {

    ip.messaging.MessageListModel = {
        init: init,
        pushMessages: pushMessages,
        pushPreviousMessages: pushPreviousMessages,
        appendMessage: appendMessage
    };

    var DateUtil = ip.common.DateUtil;

    var messageList = [];
    var messagesCallback;

    function init(messagesCallback_) {
        messagesCallback = messagesCallback_;
    }

    function appendMessages(userId, loadedMessages) {

        fixDates(loadedMessages);

        var oldMessages = messageList[userId];
        var allMessages = oldMessages != null ? loadedMessages.concat(oldMessages) : loadedMessages;

        messageList[userId] = allMessages;
        messagesCallback(allMessages);
    }

    function pushMessages(userId) {

        var messages = messageList[userId];

        if (messages != null) {
            messagesCallback(messages);
        } else {
            loadMessagesFromServer(userId, 0);
        }
    }

    function pushPreviousMessages(userId) {
        loadMessagesFromServer(userId, messageList[userId].length);
    }

    function loadMessagesFromServer(userId, offset) {
        $.ajax({
            url: GLOBAL.baseUrl + "/ajax/messaging/getMessages",
            dataType : "json",
            data: {userId: userId, offset: offset}
        })
        .done(function(messages) {
            appendMessages(userId, messages);
        });
    }

    function appendMessage(userId, message) {
        messageList[userId] = messageList[userId].concat(message);
    }

    function fixDates(messages) {
        $.each(messages, function(i, msg) {
            msg.created = DateUtil.toClientDate(new Date(msg.created), DATE_DIFF_MS);
        });
    }

})();
