(function() {

    ip.messaging.UnviewedMessageHandler = {
        init: init,
        handle: handle
    };

    var UserListManager = ip.messaging.UserListManager;

    var currentUserId;

    function init(currentUserId_) {
        currentUserId = currentUserId_;
    }

    function handle(messages) {

        var unviewedMessages = getUnviewedMessages(messages);

        if (unviewedMessages.length > 0) {
            markMessagesAsViewed(unviewedMessages);
            UserListManager.decNewMessages(unviewedMessages.length);
            sendToServer(unviewedMessages);
        }
    }

    function markMessagesAsViewed(messages) {
        for (var i = 0; i < messages.length; i++) {
            messages[i].viewed = true;
        }
    }

    function getUnviewedMessages(messages) {

        var arr = [];

        for (var i = 0; i < messages.length; i++) {
            var msg = messages[i];
            if (!msg.viewed && msg.recipient.id == currentUserId) {
                arr.push(msg);
            }
        }

        return arr;
    }

    function sendToServer(messages) {
        $.ajax({
            url: GLOBAL.baseUrl + "/ajax/messaging/markMessagesAsViewed",
            type: "POST",
            data: { messagesIds: getMessagesIds(messages) }
        });
    }

    function getMessagesIds(messages) {

        // If the array has only one element then jquery sends it not as array.
        // Thus the array should contain at least two elements.
        var arr = [-1];

        for (var i = 0; i < messages.length; i++) {
            arr.push(messages[i].id);
        }

        return arr;
    }

})();
